import React, { useState, useEffect } from 'react';
import './App.css';
import FileUpload from './components/FileUpload';
import ChatInterface from './components/ChatInterface';

function App() {
  const [isReady, setIsReady] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if documents are already processed
    checkStatus();
  }, []);

  const checkStatus = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/status');
      const data = await response.json();
      setIsReady(data.processed);
    } catch (err) {
      console.error('Failed to check status:', err);
    }
  };

  const handleFilesProcessed = () => {
    setIsReady(true);
    setError(null);
    setLoading(false);
  };

  const handleError = (errorMessage) => {
    setError(errorMessage);
    setIsReady(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>ü§ñ RAG Chat - AI PDF Assistant</h1>
        <p>Upload PDF files and start chatting with your documents</p>
      </header>

      <main className="App-main">
        {!isReady ? (
          <FileUpload 
            onFilesProcessed={handleFilesProcessed}
            onError={handleError}
            loading={loading}
            setLoading={setLoading}
          />
        ) : (
          <ChatInterface 
            onReset={() => setIsReady(false)}
          />
        )}
      </main>

      {error && (
        <div className="error-message">
          <span>‚ùå {error}</span>
        </div>
      )}
    </div>
  );
}

export default App;
